mapper.update:
update tbl_board
set board_views = board_views + 1
where BOARD_ID = #{boardId}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RN, BOARD.* FROM ( SELECT B.BOARD_ID, B.BOARD_TITLE, U.NAME, B.BOARD_VIEWS, B.BOARD_REGISTER_DATE, B.BOARD_UPDATE_DATE, U.PROVIDER_ID, (SELECT COUNT(*) FROM TBL_FILE F WHERE F.BOARD_ID = B.BOARD_ID) AS FILE_COUNT FROM TBL_BOARD B JOIN TBL_USERS U ON U.PROVIDER_ID = B.PROVIDER_ID ORDER BY
B.BOARD_VIEWS DESC B.BOARD_UPDATE_DATE ASC B.BOARD_UPDATE_DATE DESC
) BOARD WHERE ROWNUM <= #{endRow} ) WHERE rn > #{startRow}
